        function hienThiAnhLon(figure, duongDanAnhLon) {
            var anhLon = document.getElementById("anhLon");
            anhLon.src = duongDanAnhLon;

            var hinhAnhNho = document.getElementsByClassName("ProductThumbnailCarousel_image__SIlD5");

            // Loại bỏ thuộc tính 'ProductThumbnailCarousel_image-active__FuBtF' cho tất cả các thẻ <figure>
            for (var i = 0; i < hinhAnhNho.length; i++) {
                hinhAnhNho[i].classList.remove("ProductThumbnailCarousel_image-active__FuBtF");
            }

            // Thêm thuộc tính 'ProductThumbnailCarousel_image-active__FuBtF' cho thẻ <figure> được nhấp vào
            figure.classList.add("ProductThumbnailCarousel_image-active__FuBtF");
