<script>
    document.addEventListener('DOMContentLoaded', function() {
        const productPriceElement = document.getElementById('productPrice');
        const productPriceOldElement = document.getElementById('productPriceOld');
        const discountPercentElement = document.getElementById('discountPercent');

        const productPrice = parseFloat(productPriceElement.textContent);
        const productPriceOld = parseFloat(productPriceOldElement.textContent);

        const discountPercent = Math.round(((productPriceOld - productPrice) / productPriceOld) * 100);

        discountPercentElement.textContent = `-${discountPercent}%`;
    });
</script>
