                                            <script>
                                                var showPasswordIconLG = document.getElementById('showPasswordIconLG');
                                                var hidePasswordIconLG = document.getElementById('hidePasswordIconLG');
                                                var PasswordInputLG = document.querySelector('input[name="user_password"]');

                                                showPasswordIconLG.addEventListener('click', function () {
                                                    PasswordInputLG.type = 'text';
                                                    showPasswordIconLG.style.display = 'none';
                                                    hidePasswordIconLG.style.display = 'block';
                                                });

                                                hidePasswordIconLG.addEventListener('click', function () {
                                                    PasswordInputLG.type = 'password';
                                                    hidePasswordIconLG.style.display = 'none';
                                                    showPasswordIconLG.style.display = 'block';
                                                });

                                                function validateFormLogin(event){
                                                    var user_phone = document.querySelector('input[name="user_phone"]').value;
                                                    var user_pass = document.querySelector('input[name="user_password"]').value;
                                                    var checkphone = document.querySelector('.checkphone');
                                                    var checkpass = document.querySelector('.checkpass');
                                                    var specialCharRegex = /[!@#$%^&*(),.?":{}|<>]/;

                                                    if (user_phone === '') {
                                                        checkphone.textContent = 'Số điện thoại là bắt buộc. Vui lòng nhập đầy đủ.';
                                                        checkphone.style.display = 'block';
                                                        return false;
                                                    } else if (user_phone.length !== 10) {
                                                        checkphone.textContent = 'Số điện thoại không chính xác.';
                                                        checkphone.style.display = 'block';
                                                        return false;
                                                    } else {
                                                        checkphone.style.display = 'none';
