  <script>
    function changeActiveClass(element) {
        // Xóa lớp active khỏi tất cả các phần tử
        var items = document.getElementsByClassName("CategoryMenu_category-item__PH9W7");
        for (var i = 0; i < items.length; i++) {
            items[i].classList.remove("CategoryMenu_category-item-active__Z9v5b");
            items[i].querySelector("p").style.color = "";
        }
        
        // Thêm lớp active cho phần tử được chọn
        element.classList.add("CategoryMenu_category-item-active__Z9v5b");
        element.querySelector("p").style.color = "#fff";

        var allCategoryItem = document.querySelector('.hide-active');
        allCategoryItem.style.display = 'none';

        // Lấy id của danh mục cha được chọn
        var categoryId = element.getAttribute('data-category-id');

        // Lấy danh sách các danh mục con tương ứng với danh mục cha
        var categoryDetailItems = document.querySelectorAll('.CategoryMenu_product__N5Sud');
        categoryDetailItems.forEach(function(detailItem) {
            var detailCategoryId = detailItem.getAttribute('data-category-id');

            // Hiển thị danh mục con nếu có id của danh mục cha trùng khớp
            if (categoryId === detailCategoryId) {
            detailItem.style.display = 'block';
            } else {
            detailItem.style.display = 'none';
            }
        });
            }

    
</script>
